import React from "react";
import FormattedDate from "./FormattedDate";

export default function WeatherConditions(props){

    function handleSubmit(event){
      event.preventDefault();
  }

    return (
    <div>    
    <div className="row Temperature">
      <span>
        <span>
          {Math.round(props.temperature)}
        </span>
          <span className="units">
           {""} <a className="celsius" href="/" onClick={handleSubmit}>
            °C
          </a>
          {""} | {""}
          <a className="fahrenheit" href="/" onClick={handleSubmit}>
            °F
          </a>
        </span>  
      </span>
    </div>

    <div className="row Conditions">
      <ul className="col-6 Description">
        <li className="text-capitalize description">{props.description}</li>
        <li>
          Humidity:{" "}
          {props.humidity}% Wind:{" "}
          {Math.round(props.wind)} Km/H
        </li>
      </ul>
      <ul className="col-6 icon-date">
        <li><img src={props.icon} alt={props.description} /></li>
        <li className="current-date-time"><FormattedDate date={props.date} /></li>
      </ul>
    </div>
    </div>
    );
}
