import React from "react";
import "./Weather.css";

export default function Weather() {
  let currentCity = "Berlin";
  let temperature = {
    celsius: 20,
    fahrenheit: 68
  };
  let weatherData = {
    icon: <i className="fas fa-cloud-rain"></i>,
    date: "Tuesday 16 February 15:12",
    conditions: "Rainy",
    humidity: 80,
    wind: 16
  };

  function handleSubmit(event){
      event.preventDefault();
  }

  return (
    <div className="Weather">
      <h2>Current weather in {currentCity}</h2>
      <div className="row Temperature">
      <span>
        <span>
          {temperature.celsius}
        </span>
          <span className="units">
           {""} <a className="celsius" href="/" onClick={handleSubmit}>
            °C
          </a>
          {""} | {""}
          <a className="fahrenheit" href="/" onClick={handleSubmit}>
            °F
          </a>
        </span>  
      </span>
    </div>
    <div className="row Conditions">

      <ul className="col-6">
        <li className="text-capitalize description">{weatherData.conditions}</li>
        <li>
          Humidity:
          {weatherData.humidity}% Wind:
          {weatherData.wind} Km/H
        </li>
      </ul>

      <ul className="col-6">
        <li className="current-icon">{weatherData.icon}</li>
        <li className="current-date-time">{weatherData.date}</li>
      </ul>

    </div>
    </div>

  );
}
